import { useState } from 'react';
import { AuthProvider, useAuth } from './lib/AuthContext';
import { signOut } from './lib/supabase';
import AuthPage from './pages/AuthPage';
import Dashboard from './pages/Dashboard';
import MatchesPage from './pages/MatchesPage';
import ChatPage from './pages/ChatPage';
import ProfilePage from './pages/ProfilePage';
import SettingsPage from './pages/SettingsPage';
import { styles } from './pages/styles';

// ‚îÄ‚îÄ‚îÄ Layout Components ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

function Header({ profile, currentPage, setCurrentPage }) {
  const [loggingOut, setLoggingOut] = useState(false);

  async function handleLogout() {
    setLoggingOut(true);
    try { await signOut(); } catch (e) { console.error(e); }
    setLoggingOut(false);
  }

  return (
    <header style={styles.header}>
      <div style={styles.headerLogo} onClick={() => setCurrentPage('dashboard')}>
        üí´ CampusAnon
      </div>
      {profile && (
        <nav style={styles.headerNav}>
          {[
            { id:'dashboard', label:'üè† Home' },
            { id:'matches',   label:'üíû Matches' },
            { id:'chat',      label:'üí¨ Chat' },
            { id:'profile',   label:'üë§ Profile' },
          ].map(({ id, label }) => (
            <button key={id}
              style={currentPage === id ? {...styles.navBtn, ...styles.navBtnActive} : styles.navBtn}
              onClick={() => setCurrentPage(id)}>
              {label}
            </button>
          ))}
        </nav>
      )}
      {profile && (
        <div style={styles.headerRight}>
          <span style={styles.aliasTag}>{profile.alias}</span>
          <button style={styles.logoutBtn} onClick={handleLogout} disabled={loggingOut}>
            {loggingOut ? '...' : 'Logout'}
          </button>
        </div>
      )}
    </header>
  );
}

function Sidebar({ profile, currentPage, setCurrentPage }) {
  if (!profile) return null;
  const items = [
    { id:'dashboard', icon:'üè†', label:'Discover' },
    { id:'matches',   icon:'üíû', label:'My Matches' },
    { id:'chat',      icon:'üí¨', label:'Messages' },
    { id:'profile',   icon:'üë§', label:'My Profile' },
    { id:'settings',  icon:'‚öôÔ∏è', label:'Settings' },
  ];
  return (
    <aside style={styles.sidebar}>
      <div style={styles.sidebarUser}>
        <div style={styles.sidebarAvatar}>{profile.alias?.[0]}</div>
        <div>
          <div style={styles.sidebarAlias}>{profile.alias}</div>
          <div style={styles.sidebarGender}>
            {profile.gender === 'male' ? '‚ôÇ Male' : profile.gender === 'female' ? '‚ôÄ Female' : '‚öß Non-binary'}
          </div>
        </div>
      </div>
      <hr style={styles.sidebarDivider} />
      {items.map(({ id, icon, label }) => (
        <button key={id}
          style={currentPage === id ? {...styles.sidebarItem, ...styles.sidebarItemActive} : styles.sidebarItem}
          onClick={() => setCurrentPage(id)}>
          <span>{icon}</span> {label}
        </button>
      ))}
    </aside>
  );
}

function Footer() {
  return (
    <footer style={styles.footer}>
      <span>¬© 2025 CampusAnon ¬∑ Your college, anonymously connected</span>
      <span style={{marginLeft:24}}>üîí All identities are protected</span>
      <span style={{marginLeft:24}}><a href="#" style={styles.footerLink}>Privacy Policy</a> ¬∑ <a href="#" style={styles.footerLink}>Terms</a></span>
    </footer>
  );
}

// ‚îÄ‚îÄ‚îÄ Inner App (has auth context access) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

function InnerApp() {
  const { profile, loading } = useAuth();
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [activeChatPartner, setActiveChatPartner] = useState(null);

  if (loading) {
    return (
      <div style={styles.appWrap}>
        <div style={styles.loadingWrap}>Loading CampusAnon...</div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div style={styles.appWrap}>
        <Header profile={null} currentPage={currentPage} setCurrentPage={setCurrentPage} />
        <AuthPage />
        <Footer />
      </div>
    );
  }

  return (
    <div style={styles.appWrap}>
      <Header profile={profile} currentPage={currentPage} setCurrentPage={setCurrentPage} />
      <div style={styles.body}>
        <Sidebar profile={profile} currentPage={currentPage} setCurrentPage={setCurrentPage} />
        <main style={styles.main}>
          {currentPage === 'dashboard' && <Dashboard />}
          {currentPage === 'matches'   && <MatchesPage setCurrentPage={setCurrentPage} setActiveChatPartner={setActiveChatPartner} />}
          {currentPage === 'chat'      && <ChatPage activeChatPartner={activeChatPartner} setActiveChatPartner={setActiveChatPartner} />}
          {currentPage === 'profile'   && <ProfilePage />}
          {currentPage === 'settings'  && <SettingsPage />}
        </main>
      </div>
      <Footer />
    </div>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <InnerApp />
    </AuthProvider>
  );
}
